const fs = require(''fs'');
const extracted = JSON.parse(fs.readFileSync(''scripts/extracted-abilities-final.json'', ''utf-8''));

const testAbilities = [''Vesting Faith'', ''Stalwart'', ''Holy Emanation I'', ''Thaumaturge''];

testAbilities.forEach(name => {
  const ability = extracted.find(a => a.codeName === name);
  if (ability) {
    console.log(''\n'' + ''=''.repeat(60));
    console.log(ability.codeName);
    console.log(''=''.repeat(60));
    console.log(''Class:'', ability.class);
    console.log(''Prerequisites:'', ability.prerequisites || ''(none)'');
    console.log(''\nDescription:'');
    console.log(ability.description);
  }
});
")
